import React, { useState } from 'react';

function App() {
  const [imageNumber, setImageNumber] = useState(1000);
  const [mergedImageUrl, setMergedImageUrl] = useState('');
  const [ethereumAddress, setEthereumAddress] = useState('');

  const handleMergeImages = async () => {
    try {
      const mergeResponse = await fetch(`http://localhost:3001/merge?imageNumber=${imageNumber}`);
      if (mergeResponse.ok) {
        const mergedImageBlob = await mergeResponse.blob();
        const mergedImageUrl = URL.createObjectURL(mergedImageBlob);
        setMergedImageUrl(mergedImageUrl);
      } else {
        throw new Error('Error merging images');
      }

      try {
        const furResponse = await fetch(`http://localhost:3004/api/${imageNumber}`);
        if (furResponse.ok) {
          const furTraitValue = await furResponse.json();
          console.log('Fur Trait Value:', furTraitValue);

          // Update the additionalLayerImage URL based on furTraitValue
          const additionalLayerImageUrls = {
            Alien: 'https://upcdn.io/W142i9c/raw/background/Alien.png',
            Black: 'https://upcdn.io/W142i9c/raw/background/Black.png',
            Giraffe: 'https://upcdn.io/W142i9c/raw/background/Giraffe.png',
            Gold: 'https://upcdn.io/W142i9c/raw/background/Gold.png',
            Grey: 'https://upcdn.io/W142i9c/raw/background/Grey.png',
            Green: 'https://upcdn.io/W142i9c/raw/background/Green.png',
            Iceberg: 'https://upcdn.io/W142i9c/raw/background/Iceberg.png',
            Original: 'https://upcdn.io/W142i9c/raw/background/Original.png',
            Pink: 'https://upcdn.io/W142i9c/raw/background/Pink.png',
            Slime: 'https://upcdn.io/W142i9c/raw/background/Slime.png',
            Snow: 'https://upcdn.io/W142i9c/raw/background/Snow.png',
            Tiger: 'https://upcdn.io/W142i9c/raw/background/Tiger.png',
            Zebra: 'https://upcdn.io/W142i9c/raw/background/Zebra.png',
            Purple: 'https://upcdn.io/W142i9c/raw/background/Purple.png',
          };

          const additionalLayerImageUrl = additionalLayerImageUrls[furTraitValue];
          if (additionalLayerImageUrl) {
            // Use additionalLayerImageUrl to fetch the image or perform any actions
            console.log('Additional Layer Image URL:', additionalLayerImageUrl);
          } else {
            console.error('Invalid furTraitValue:', furTraitValue);
          }
        } else {
          throw new Error('Error fetching fur trait');
        }
      } catch (error) {
        console.error('Error fetching fur trait:', error);
      }
    } catch (error) {
      console.error('Error merging images:', error);
    }
  };

  const handleDownload = () => {
    const link = document.createElement('a');
    link.href = mergedImageUrl;
    link.download = 'merged_image.png';
    link.click();
  };

  const handleEthereumAddressChange = (e) => {
    setEthereumAddress(e.target.value);
  };

  const handleDropEthereumAddress = () => {
    // Perform Ethereum-related action here
    console.log('Dropping Ethereum address:', ethereumAddress);
  };

  return (
    <div className="App">
      <h1>Meme-Gen</h1>
      <label htmlFor="imageNumber">Enter Doge Number:</label>
      <input
        type="number"
        id="imageNumber"
        min="1"
        step="1"
        value={imageNumber}
        onChange={(e) => setImageNumber(e.target.value)}
      />
      <button onClick={handleMergeImages}>Generate Meme</button>
      {mergedImageUrl && (
        <div>
          <img
            src={mergedImageUrl}
            alt="Merged"
            style={{ maxWidth: '40%', marginTop: '20px' }}
          />
          <button onClick={handleDownload}>Download Merged Image</button>
        </div>
      )}
      <div>
        <label htmlFor="ethereumAddress">Enter Ethereum Address:</label>
        <input
          type="text"
          id="ethereumAddress"
          value={ethereumAddress}
          onChange={handleEthereumAddressChange}
        />
        <button onClick={handleDropEthereumAddress}>Drop Ethereum Address</button>
      </div>
    </div>
  );
}

export default App;
