import React, { useState } from 'react';
import axios from 'axios';

const API_KEY = '85a320bbc3e447ebb881a2ce01741784'; // Replace this with your actual API key

const App = () => {
  const [nftId, setNftId] = useState('');
  const [traits, setTraits] = useState([]);

  const handleChange = (event) => {
    setNftId(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const apiUrl = `https://api.opensea.io/api/v1/asset/0xf4ee95274741437636e748ddac70818b4ed7d043/${nftId}/`;

    try {
      const response = await axios.get(apiUrl, {
        headers: {
          'X-API-KEY': API_KEY,
        },
      });

      const traitMetadata = response.data.traits
        .filter(
          (trait) =>
            trait.trait_type.toLowerCase() === 'fur' // Include only traits related to fur
        )
        .map((trait) => ({
          ...trait,
          trait_type: trait.trait_type.toLowerCase(),
          value: trait.value.toLowerCase().replace(/\s+/g, ''),
        }));

      setTraits(traitMetadata);
    } catch (error) {
      console.error('Error while fetching NFT trait metadata:', error.message);
    }
  };

  return (
    <div className="App">
      <h1>Meme-Gen</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="nftId">Enter NFT ID:</label>
        <input
          type="text"
          id="nftId"
          value={nftId}
          onChange={handleChange}
        />
        <button type="submit">Fetch Traits</button>
      </form>
      {traits.length > 0 && (
        <div>
          <h2>Fur Traits:</h2>
          <ul>
            {traits.map((trait, index) => (
              <li key={index}>
                <strong>Trait Type:</strong> {trait.trait_type},{' '}
                <strong>Value:</strong> {trait.value}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
